package Exercises;

public class QuartsToGallons {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int QTS_INT_GALLONS = 4;
		int NumberOfQuartsNeeded = 18;
		int NumberOfGallons;
		int NumberOfQuarts;
		
		NumberOfGallons = NumberOfQuartsNeeded / QTS_INT_GALLONS;
		NumberOfQuarts = NumberOfQuartsNeeded % QTS_INT_GALLONS;
		
		System.out.println(" The total number of gallons is " + NumberOfGallons);
		System.out.println(" The total number of quarts is " + NumberOfQuarts);
		System.out.println("A job that needs 18 quarts requires 4 gallons plus 2 quarts.");
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
